$(function() {
    $(document).on("click", ".category-filter__has-child > a", function(e) {
        if ($(window).width() >= '768'){
            e.preventDefault();
            var t = $(this).closest(".category-filter__has-child"),
                i = t.find(".category-filter__child");
            if (0 == i.length) return !1;
            t.hasClass("active") ? (t.removeClass("active"), i.stop().slideUp()) : (t.addClass("active"), i.stop().slideDown())
        }
    }), $(document).on("click", ".category-filter__submit, .product-list__events a", function(e) {
        e.preventDefault(), $(this).toggleClass("active")
    }), $(document).on("click", ".mobile-filter--filter", function(e) {
        e.preventDefault();
        var t = $(this),
            i = t.closest(".mobile-filter"),
            l = i.find(".mobile-filter__filter-content");
        if (0 == l.length) return !1;
        t.hasClass("active") ? (t.removeClass("active"), l.stop().slideUp()) : (t.addClass("active"), l.stop().slideDown(), $(".mobile-filter--category").removeClass("active"), $(".mobile-filter__category-content").stop().slideUp(), $(document).mouseup(function(e) {
            0 === i.has(e.target).length && (t.removeClass("active"), l.stop().slideUp())
        }))
    }), $(document).on("click", ".mobile-filter--category", function(e) {
        e.preventDefault();
        var t = $(this),
            i = t.closest(".mobile-filter"),
            l = i.find(".mobile-filter__category-content");
        if (console.log(l), 0 == l.length) return !1;
        t.hasClass("active") ? (t.removeClass("active"), l.stop().slideUp()) : (t.addClass("active"), l.stop().slideDown(), $(".mobile-filter--filter").removeClass("active"), $(".mobile-filter__filter-content").stop().slideUp(), $(document).mouseup(function(e) {
            0 === i.has(e.target).length && (t.removeClass("active"), l.stop().slideUp())
        }))
    })
});