$(function() {
    $(".product-cart-carousel").owlCarousel({
        loop: !0,
        margin: 5,
        nav: !1,
        thumbs: !0,
        rtl:true, //на англ версии его не должно быть
        thumbsPrerendered: !0,
        thumbContainerClass: "owl-thumbs",
        responsive: {
            0: {
                items: 1
            }
        }
    }),
    $(".product-cart-carousel2").owlCarousel({
        loop: !0,
        margin: 15,
        nav: !0,
        rtl:true, //на англ версии его не должно быть
        thumbs: !0,
        thumbsPrerendered: !0,
        thumbContainerClass: "owl-thumbs2",
        responsive: {
            0: {
                items: 1
            }
        }
    }), $(document).on("click", ".size-select a", function(e) {
        e.preventDefault(), $(this).closest(".size-select").find("a").removeClass("active"), $(this).addClass("active")
    }), $(document).on("click", ".addclassbody", function(e) {
        $('body').addClass('modal-open');
    }), $(document).on("click", ".page-content__by_one-click", function(e) {
        e.preventDefault(), $("#one-click").modal("show")
    }), $(document).on("click", ".inst-pay", function(e) {
        e.preventDefault(), $("#instantpayment").modal("show")
    }), $(document).on("click", ".pass-recovery-modal", function(e) {
        e.preventDefault(), $("#pass-recovery").modal("show")
    }), $(document).on("click", ".thanks-modal-open", function(e) {
        e.preventDefault(), $("#thanks-modal").modal("show")
    }), $(document).on("click", ".special-offer-modal-open", function(e) {
        e.preventDefault(), $("#special-offer").modal("show")
    }), $(document).on("click", ".sign-in-modal-open", function(e) {
        e.preventDefault(), $("#sign-in").modal("show")
    }), $(document).on("click", ".register-modal-open", function(e) {
        e.preventDefault(), $("#register-modal").modal("show")
    }), $(document).on("click", ".fast-seen-modal-open", function(e) {
        e.preventDefault(), $("#fast-seen").modal("show")
    }), $(document).on("click", ".gallerry-product-modal-open", function(e) {
        e.preventDefault(), $("#gallerry-product").modal("show");
            $("#fast-seen").modal("hide");
             $('#gallerry-product .slider-for').slick('reinit');
             $('#gallerry-product .slick-prev').click();
    }), $(document).on("click", ".change-pass-modal-open", function(e) {
        e.preventDefault(), $("#change-pass").modal("show")
    }), $(document).on("click", ".individual-inscription__button", function(e) {
        e.preventDefault();
        var i = $(this).prev(),
            n = $(this).parent().next().find('input').val(),
            o = $(this).prev().children(".individual-inscription__field-item").length,
            t = $('<div class="individual-inscription__field-item"><div class="close-input"></div>\n<input type="text" placeholder="' + $(this).attr('data-placeholder-input') + '">\n</div>');
            t.find("span").text(o + 1 + "."), i.append(t);
    }), $(document).on("click", ".available-options .available-options__main-link", function(e) {
        e.preventDefault();
        var i = $(this).closest(".available-options"),
            o = i.find(".available-options__list");
        i.hasClass("active") ? (i.removeClass("active"), o.stop().slideUp()) : (i.addClass("active"), o.stop().slideDown())
    }), $(document).on("click", ".choose__size", function(e) {
        e.preventDefault(), $("#choose-size").modal("show")
    }), $(document).on("click", ".choose__color", function(e) {
        e.preventDefault(), $("#choose-color").modal("show")
    }), $(document).on("click", ".choose__description", function(e) {
        e.preventDefault(), $("#choose-description").modal("show")
    });

   
    $('.individual-inscription__fields').on('click', '.close-input', function() {
        $(this).parent().remove();
    });
    $('.choose').on('click', '.open-drop', function(e) {
        e.preventDefault();
        $('.choose__dropdowns.opened').not($(this).next()).fadeOut(300);
        $('.choose__dropdowns.opened').not($(this).next()).removeClass("opened");
        $(this).next().fadeIn(300);
        $(this).next().addClass("opened");
        $('.choose-overlay').fadeIn(300);
    });
    $('.choose').on('click', '.choose-overlay', function(e) {
        $('.choose__dropdowns.opened').fadeOut(300);
        $('.choose__dropdowns.opened').removeClass("opened");
        $(this).fadeOut(300);
    });
    $('.choose').on('click', '.exit-modal', function(e) {
        e.preventDefault();
        $('.choose__dropdowns.opened').fadeOut(300);
        $('.choose__dropdowns.opened').removeClass("opened");
        $(".choose-overlay").fadeOut(300);
    });
});

$(document).ready(function() {
      $('#gallerry-product .slider-for').slick({
          slidesToShow: 1,
          slidesToScroll: 1,
            arrows: true,
          asNavFor: '.slider-nav'
        });
        $('#gallerry-product .slider-nav').slick({
          slidesToShow: 4,
          slidesToScroll: 1,
          asNavFor: '.slider-for',
          centerMode: true,
          focusOnSelect: true
        });  
    });

